              </div>
              <FormControlLabel
                control={
                  <Switch
                    id="published"
                    name="published"
                    checked={isPublishedFilterOn}
                    value={isPublishedFilterOn}
                    onChange={handlePublishedFilterChange}
                    color="secondary"
                  />
                }
